*{box-sizing: border-box;margin:0 ; padding: 0;}
*::before,*::after{box-sizing: border-box;}
.wrap{
  margin: 30px auto;
  position: relative;
  width: 400px;
  height:500px;
}
.head{
  position: absolute;
  left: 100px;
  width: 230px;
  height: 200px;
  background: #00A1FF;
  border-radius: 50%;
  border: 2px solid black;
}
.face{
  position: absolute;
  left: 20px;
  top: 50px;
  width: 190px;
  height: 145px;
  background: #fff;
  border-radius: 50% 50% 45% 45% / 50% 50% 50% 50%;
  border: 2px solid black;
}
.eyel{
  position: absolute;
  left: 46px;
  top:-22px;
  width: 40px;
  height: 50px;
  border: 2px solid black;
  border-radius: 45%;
  background: #fff;
  z-index: 3;
}
.eyer{
  position: absolute;
  top: 30px;
  left: 106px;
  width: 40px;
  height: 50px;
  border: 2px solid black;
  border-radius: 45%;
  background: #fff;
  z-index: 4;
}
.eyeball{
  position: absolute;
  left: 18px;
  top: 18px;
  width: 20px;
  height: 20px;
  background: black;
  border-radius: 50%;
  transition: all .5s;
}
.wrap:hover .eyeball{
  animation:  round 2s infinite linear;
}
@keyframes round{
  50%{ transform: translateX(-15px);}
}
.nose{
  position: absolute;
  left: 98px;
  top: 70px;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #f00;
  border: 2px solid black;
  z-index: 6;
}
.nose_w{
  position: absolute;
  left: 8px;
  top: 2px;
  width: 10px;
  height: 10px;
  background: #fff;
  border-radius: 50%;
}
.nose_d{
  position: absolute;
  left: 10px;
  top: 20px;
  width: 2px;
  height: 70px;
  background: #000;
}
.whiskers{
  position: absolute;
  left: 48px;
  top: 80px;
  width: 130px;
  height: 80px;
   /* border: 1px solid gray;  */
  z-index: 1;
}
.whiskers > span{
  position: relative;
  display: inline-block;
  width: 50px;
  height: 2px;
  background: black;
}
.whiskers > span:nth-of-type(1){
  transform: rotate(20deg) translate(-10px,0);
}

.whiskers > span:nth-of-type(2){
  transform: rotate(-20deg) translate(30px,8px);
}
.whiskers > span:nth-of-type(3){
  transform: translate(-10px,-5px);
}
.whiskers > span:nth-of-type(4){
  transform: translate(32px,-5px);
}
.whiskers > span:nth-of-type(5){
  transform: rotate(-20deg) translate(-6px,-9px);
}
.whiskers > span:nth-of-type(6){
  transform: rotate(15deg) translate(30px,-17px);
}
.mouth{
  position: absolute;
  left: 60px;
  top: 74px;
  width: 100px;
  height: 90px;
  background: white;
  border-radius: 50%;
  box-shadow: 0 3px 0 0 black;

  /* z-index: -1; */
}
.mouthl{
  position: absolute;
  top: 40px;
  left: -5px;
  transform: rotate(-22deg);
  width: 10px;
  height: 30px;
  background:white;
}
.mouthr{
  position: absolute;
  left: 95px;
  top: 40px;
  transform: rotate(20deg);
  width: 10px;
  height: 30px;
  background:white;
}
